jQuery.fn.textPlaceholder=function(){return this.each(function(){var b=this;if(b.placeholder&&"placeholder" in document.createElement(b.tagName)){return}var c=b.getAttribute("placeholder");var a=$(b);if(b.value===""||b.value==c){a.addClass("text-placeholder");b.value=c}a.focus(function(){if(a.hasClass("text-placeholder")){this.value="";a.removeClass("text-placeholder")}});a.blur(function(){if(this.value===""){a.addClass("text-placeholder");this.value=c}});b.form&&$(b.form).submit(function(){if(a.hasClass("text-placeholder")){b.value=""}})})};